    @Test
    public void parseJson() {
        String jsonData = "{\"payload\":{\"ret\":1,\"photo\":{\"756220591\":{\"replys\":{\"list\":{},\"more\":0},\"vote_num\":29,\"reply_num\":0,\"share_num\":0,\"original_url\":\"http:\\/\\/p1-q.mafengwo.net\\/s14\\/M00\\/C0\\/84\\/wKgE2l0osSGAQnG3ABGwsP8AOmE582.jpg\",\"is_vote\":1}}},\"resource\":{\"css\":[],\"js\":[]}}";

        String data = new JsonPathSelector("$.payload").select(jsonData);
        log.info(data);

        String scenceNum = "756220591";
        String voteNum = new JsonPathSelector("$.payload.photo." + scenceNum + ".vote_num").select(jsonData);
        String replyNum = new JsonPathSelector("$.payload.photo." + scenceNum + ".reply_num").select(jsonData);
        String shareNum = new JsonPathSelector("$.payload.photo." + scenceNum + ".share_num").select(jsonData);
        String originalUrl = new JsonPathSelector("$.payload.photo." + scenceNum + ".original_url").select(jsonData);

        PhotoInfo photoInfo = new PhotoInfo();
        photoInfo.setVoteNum(Integer.valueOf(voteNum));
        photoInfo.setReplyNum(Integer.valueOf(replyNum));
        photoInfo.setShareNum(Integer.valueOf(shareNum));
        photoInfo.setOriginalUrl(originalUrl);

        log.info(photoInfo.toString());


        String url = "http://www.mafengwo.cn/photo/11703/scenery_15790467/796108384.html";

        String imageNumber = url.substring(url.lastIndexOf("/") + 1, url.lastIndexOf("."));

        log.info(imageNumber);

        String noteNumber = url.substring(url.lastIndexOf("scenery_") + 8, url.lastIndexOf("/"));
        log.info(noteNumber);


        log.info(extractTravelImageRequestFromUrl(url).toString());
    }

